$("#checkNumber").on('click', function () {
    var that = this;
    $(that).find('.spinner-border').show();
    $(that).attr('disabled', 'disabled');
    var txtBillingNumber = $("#BillingNumber").val();
    if (txtBillingNumber != "") {
        debugger;
        var billInquiryService = abp.services.app.billInquiries;
        billInquiryService.getBillInquiryByBillingNumber(txtBillingNumber).done(function (data) {
            debugger;
            if (data.responseCode == "000") {

                window.location.href = 'BillInquiryWeb?billingNumber=' + txtBillingNumber;
            } else {
                abp.notify.error(data.responseMessage);
                $(that).removeAttr('disabled');
                $(that).find('.spinner-border').hide();
            }

        }).fail(function (error) {
            debugger;
            abp.notify.error(error);
            $(that).removeAttr('disabled');
            $(that).find('.spinner-border').hide();
        });

    } else {
        abp.notify.error(app.localize('Billing Number Required Field'));
        $(that).removeAttr('disabled');
        $(that).find('.spinner-border').hide();
    }

});
